import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import ForgotPassword from './components/ForgotPassword'; // Assuming you have this component
import AddCategory from './components/AddCategory';
import EditCategory from './components/EditCategory';
import AddSubcategory from './components/AddSubcategory'; 
import './App.css';

function App() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/categories');
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const addCategory = (newCategory) => {
    setCategories([...categories, newCategory]);
  };

  const updateCategory = (updatedCategory) => {
    setCategories(categories.map(category =>
      category.id === updatedCategory.id ? updatedCategory : category
    ));
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/dashboard" element={<Dashboard categories={categories} />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/" element={<Login />} /> {/* Default route */}
            <Route path="/add-category" element={<AddCategory addCategory={addCategory} />} />
            <Route path="/edit-category/:id" element={<EditCategory categories={categories} updateCategory={updateCategory} />} />
            <Route path="/add-subcategory" element={<AddSubcategory addSubcategory={addSubcategory} />} />
          </Routes>
        </header>
      </div>
    </Router>
  );
}

export default App;
