import React, { useState, useEffect } from 'react';
import { useNavigate, Route, Routes } from 'react-router-dom';
import AddCategory from './AddCategory';
import EditCategory from './EditCategory';
import './Dashboard.css';

const Dashboard = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [categories, setCategories] = useState([]);
    const [activeSection, setActiveSection] = useState('home');
    const navigate = useNavigate();

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/categories');
            const data = await response.json();
            setCategories(data);
        } catch (error) {
            console.error('Error fetching categories:', error);
        }
    };

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleAdd = () => {
        navigate('/add-category');
    };

    const handleEdit = (id) => {
        navigate(`/edit-category/${id}`);
    };

    const handleDelete = async (id) => {
        try {
            const response = await fetch(`http://localhost:5000/api/categories/${id}`, {
                method: 'DELETE'
            });

            if (response.ok) {
                setCategories(categories.filter(category => category.id !== id));
                alert('Category deleted successfully');
            } else {
                alert('Failed to delete category');
            }
        } catch (error) {
            console.error('Error deleting category:', error);
            alert('Failed to delete category');
        }
    };

    const handleLogout = () => {
        // Perform any logout logic here, such as clearing user data
        alert('Logged out');
        navigate('/login');
    };

    const addCategory = (newCategory) => {
        setCategories([...categories, newCategory]);
    };

    const updateCategory = (updatedCategory) => {
        setCategories(categories.map(category => 
            category.id === updatedCategory.id ? updatedCategory : category
        ));
    };

    const filteredCategories = categories.filter(category =>
        category.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="dashboard">
            <div className="sidebar">
                <ul>
                    <li onClick={() => setActiveSection('home')}>Dashboard Home</li>
                    <li onClick={() => setActiveSection('category')}>Category</li>
                    <li>Subcategory</li>
                    <li>Products</li>
                </ul>
            </div>
            <div className="main-content">
                <div className="top-bar">
                    <button className="logout-button" onClick={handleLogout}>Log Out</button>
                </div>
                <div className="content">
                    {activeSection === 'home' && (
                        <div>
                            <h2>Welcome to the Dashboard</h2>
                            {/* Main content for the home section goes here */}
                        </div>
                    )}
                    {activeSection === 'category' && (
                        <div className="category-container">
                            <div className="search-add-container">
                                <input
                                    type="text"
                                    placeholder="Search..."
                                    value={searchTerm}
                                    onChange={handleSearch}
                                />
                                <button onClick={handleAdd}>Add</button>
                            </div>
                            <table className="category-table">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Category Name</th>
                                        <th>Image</th>
                                        <th>Status</th>
                                        <th>Sequence</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredCategories.map(category => (
                                        <tr key={category.id}>
                                            <td>{category.id}</td>
                                            <td>{category.name}</td>
                                            <td><img src={`http://localhost:5000${category.image}`} alt={category.name} width="50" /></td>
                                            <td>{category.status}</td>
                                            <td>{category.sequence}</td>
                                            <td>
                                                <button onClick={() => handleEdit(category.id)}>Edit</button>
                                                <button onClick={() => handleDelete(category.id)}>Delete</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>
            <Routes>
                <Route path="/add-category" element={<AddCategory addCategory={addCategory} />} />
                <Route path="/edit-category/:id" element={<EditCategory categories={categories} updateCategory={updateCategory} />} />
            </Routes>
        </div>
    );
};

export default Dashboard;
