import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Category = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [categories, setCategories] = useState([
        // Sample data
        { id: 1, name: 'Category 1', image: 'image1.jpg', status: 'Active', sequence: 1 },
        { id: 2, name: 'Category 2', image: 'image2.jpg', status: 'Inactive', sequence: 2 },
    ]);
    const history = useHistory();

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleAdd = () => {
        history.push('/add-category');
    };

    const handleEdit = (id) => {
        // Handle edit logic
    };

    const handleDelete = (id) => {
        // Handle delete logic
    };

    const filteredCategories = categories.filter(category =>
        category.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div>
            <div>
                <input
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearch}
                />
                <button onClick={handleAdd}>Add</button>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Category Name</th>
                        <th>Image</th>
                        <th>Status</th>
                        <th>Sequence</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredCategories.map(category => (
                        <tr key={category.id}>
                            <td>{category.id}</td>
                            <td>{category.name}</td>
                            <td><img src={category.image} alt={category.name} width="50" /></td>
                            <td>{category.status}</td>
                            <td>{category.sequence}</td>
                            <td>
                                <button onClick={() => handleEdit(category.id)}>Edit</button>
                                <button onClick={() => handleDelete(category.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Category;
